#!/bin/bash
# Monitor bundle size and Core Web Vitals metrics during development, blocking deployments that exceed performance budgets with detailed reports. Automatically analyzes Next.js build output, checks bundle sizes against predefined budgets, and provides optimization recommendations. Hook triggers on PostToolUse for build-related operations and file changes that could affect performance.

bash -c 'input=$(cat); COMMAND=$(echo "$input" | jq -r ".tool_input.command // empty"); SUCCESS=$(echo "$input" | jq -r ".tool_response.success // false"); if [ "$SUCCESS" = "true" ] && [[ "$COMMAND" =~ (npm\ run\ build|next\ build|vercel\ build|yarn\ build) ]]; then echo "üìä Performance Budget Guard: Analyzing build output..."; if [ -d ".next" ]; then BUNDLE_SIZE=$(find .next/static/chunks -name "*.js" -exec stat -f%z {} + 2>/dev/null | awk "{total += \$1} END {print total}"); BUNDLE_SIZE_KB=$((BUNDLE_SIZE / 1024)); BUDGET_LIMIT=350; echo "üì¶ Total bundle size: ${BUNDLE_SIZE_KB}KB"; if [ $BUNDLE_SIZE_KB -gt $BUDGET_LIMIT ]; then echo "üö® PERFORMANCE BUDGET EXCEEDED!"; echo "Current bundle size: ${BUNDLE_SIZE_KB}KB"; echo "Budget limit: ${BUDGET_LIMIT}KB"; echo "Overage: $((BUNDLE_SIZE_KB - BUDGET_LIMIT))KB"; echo "" >&2; echo "‚ö†Ô∏è Performance budget exceeded by $((BUNDLE_SIZE_KB - BUDGET_LIMIT))KB!" >&2; echo "" >&2; echo "üìã Bundle Analysis:" >&2; find .next/static/chunks -name "*.js" -exec ls -lah {} + 2>/dev/null | sort -k5 -hr | head -5 >&2; echo "" >&2; echo "üí° Optimization recommendations:" >&2; echo "‚Ä¢ Use dynamic imports for large components" >&2; echo "‚Ä¢ Implement code splitting with next/dynamic" >&2; echo "‚Ä¢ Check for duplicate dependencies" >&2; echo "‚Ä¢ Optimize third-party libraries" >&2; echo "‚Ä¢ Run: npm run analyze for detailed bundle analysis" >&2; exit 2; else echo "‚úÖ Bundle size within budget: ${BUNDLE_SIZE_KB}KB / ${BUDGET_LIMIT}KB"; REMAINING=$((BUDGET_LIMIT - BUNDLE_SIZE_KB)); echo "üéØ Remaining budget: ${REMAINING}KB"; if [ $REMAINING -lt 50 ]; then echo "‚ö†Ô∏è Warning: Less than 50KB remaining in performance budget"; fi; fi; else echo "‚ùå No .next build directory found. Run build first."; fi; else echo "‚ÑπÔ∏è Performance check skipped (not a build command or failed build)"; fi'
